import { useState, useEffect } from 'react';
import { collection, query, where, getDocs, limit, doc, updateDoc } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import Card from '../../components/Card';
import PricePopup from '../../components/PricePopup';
import '../../styles/dashboard.css';

const UserDashboard = () => {
  const [workers, setWorkers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [bookings, setBookings] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const workersRef = collection(db, 'workers');
        const q = query(workersRef, limit(4));
        const querySnapshot = await getDocs(q);

        const workersData = [];
        querySnapshot.forEach((doc) => {
          workersData.push({ id: doc.id, ...doc.data() });
        });

        setWorkers(workersData);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching workers:", error);
        setLoading(false);
      }
    };

    const fetchBookings = async () => {
      if (!user) return;

      try {
        const bookingsRef = collection(db, 'bookings');
        const q = query(bookingsRef, where('userId', '==', user.uid));
        const querySnapshot = await getDocs(q);

        const bookingsData = [];
        querySnapshot.forEach((doc) => {
          bookingsData.push({ id: doc.id, ...doc.data() });
        });

        setBookings(bookingsData);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchWorkers();
    fetchBookings();
  }, [user]);

  const handleBook = (worker) => {
    setSelectedWorker(worker);
    setShowPopup(true);
  };

  const handleBookingSubmit = async (bookingData) => {
    try {
      // Here you would add the booking to Firestore
      // For now, we'll just log it
      console.log('Booking data:', {
        ...bookingData,
        userId: user.uid,
        userName: user.name || user.email,
        status: 'pending', // Changed to pending for worker approval
        createdAt: new Date()
      });

      alert('Booking request sent! Worker will respond soon.');
      setShowPopup(false);
    } catch (error) {
      console.error('Error creating booking:', error);
      alert('Error creating booking');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    try {
      // Update booking status to cancelled
      const bookingRef = doc(db, 'bookings', bookingId);
      await updateDoc(bookingRef, {
        status: 'cancelled',
        cancelledAt: new Date()
      });

      // Update local state
      setBookings(bookings.map(booking => 
        booking.id === bookingId ? {...booking, status: 'cancelled'} : booking
      ));

      alert('Booking cancelled successfully!');
    } catch (error) {
      console.error('Error cancelling booking:', error);
      alert('Error cancelling booking');
    }
  };

  const getStatusBadge = (status) => {
    switch(status) {
      case 'pending':
        return <span className="status pending">Pending Approval</span>;
      case 'confirmed':
        return <span className="status confirmed">Confirmed</span>;
      case 'completed':
        return <span className="status completed">Completed</span>;
      case 'rejected':
        return <span className="status rejected">Rejected</span>;
      case 'cancelled':
        return <span className="status cancelled">Cancelled</span>;
      default:
        return <span className="status unknown">Unknown</span>;
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome, {user?.name || user?.email}</h1>
        <p>Here's what's happening with your account today.</p>
      </div>

      <div className="dashboard-sections">
        <section className="recommended-workers">
          <h2>Recommended Workers</h2>
          {loading ? (
            <div className="loading">Loading workers...</div>
          ) : (
            <div className="workers-grid">
              {workers.map((worker) => (
                <div key={worker.id} className="worker-card-wrapper">
                  <Card worker={worker} />
                  <button 
                    className="book-now-btn"
                    onClick={() => handleBook(worker)}
                  >
                    Book Now
                  </button>
                </div>
              ))}
            </div>
          )}
        </section>

        <section className="upcoming-bookings">
          <h2>Your Bookings</h2>
          {bookings.length === 0 ? (
            <div className="no-bookings">
              <p>You don't have any bookings yet.</p>
              <button onClick={() => window.location.href = '/hire'}>Find Workers</button>
            </div>
          ) : (
            <div className="bookings-list">
              {bookings.map((booking) => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-header">
                    <h3>{booking.workerName}</h3>
                    {getStatusBadge(booking.status)}
                  </div>
                  <div className="booking-details">
                    <p><strong>Service:</strong> {booking.workerProfession}</p>
                    <p><strong>Date:</strong> {booking.date}</p>
                    <p><strong>Time:</strong> {booking.time}</p>
                    <p><strong>Duration:</strong> {booking.hours} hours</p>
                    <p><strong>Total:</strong> ${booking.total}</p>
                  </div>
                  <div className="booking-actions">
                    {booking.status === 'pending' && (
                      <button 
                        className="cancel-btn"
                        onClick={() => handleCancelBooking(booking.id)}
                      >
                        Cancel Booking
                      </button>
                    )}
                    {booking.status === 'confirmed' && (
                      <button 
                        className="complete-btn"
                        onClick={() => handleCompleteBooking(booking.id)}
                      >
                        Mark as Completed
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>

      {showPopup && selectedWorker && (
        <PricePopup 
          worker={selectedWorker} 
          onClose={() => setShowPopup(false)}
          onBook={handleBookingSubmit}
        />
      )}
    </div>
  );
};

export default UserDashboard;
