import { useState, useEffect } from 'react';
import { doc, getDoc, collection, query, where, getDocs, updateDoc } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import '../../styles/dashboard.css';

const WorkerDashboard = () => {
  const [workerData, setWorkerData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [bookings, setBookings] = useState([]);
  const [newBookings, setNewBookings] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    const fetchWorkerData = async () => {
      if (!user) return;

      try {
        const workerRef = doc(db, 'workers', user.uid);
        const workerSnap = await getDoc(workerRef);

        if (workerSnap.exists()) {
          setWorkerData(workerSnap.data());
        } else {
          // Redirect to create profile if worker profile doesn't exist
          window.location.href = '/worker-profile';
        }
      } catch (error) {
        console.error("Error fetching worker data:", error);
      } finally {
        setLoading(false);
      }
    };

    const fetchBookings = async () => {
      if (!user) return;

      try {
        const bookingsRef = collection(db, 'bookings');
        const q = query(bookingsRef, where('workerId', '==', user.uid));
        const querySnapshot = await getDocs(q);

        const bookingsData = [];
        const pendingBookings = [];

        querySnapshot.forEach((doc) => {
          const booking = { id: doc.id, ...doc.data() };
          bookingsData.push(booking);
          if (booking.status === 'pending') {
            pendingBookings.push(booking);
          }
        });

        setBookings(bookingsData);
        setNewBookings(pendingBookings);

        // Show notifications for new pending bookings
        if (pendingBookings.length > 0) {
          toast.info(`You have ${pendingBookings.length} new booking requests`, {
            autoClose: false,
            closeOnClick: false
          });
        }
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchWorkerData();
    fetchBookings();

    // Set up real-time listener for new bookings
    const unsubscribe = onSnapshot(
      query(collection(db, 'bookings'), where('workerId', '==', user.uid), where('status', '==', 'pending')),
      (snapshot) => {
        const newPendingBookings = [];
        snapshot.docChanges().forEach((change) => {
          if (change.type === 'added') {
            newPendingBookings.push({ id: change.doc.id, ...change.doc.data() });
          }
        });

        if (newPendingBookings.length > 0) {
          setNewBookings(prev => [...prev, ...newPendingBookings]);
          toast.info(`You have ${newPendingBookings.length} new booking request(s)`, {
            autoClose: false,
            closeOnClick: false
          });
        }
      }
    );

    return () => unsubscribe();
  }, [user]);

  const handleBookingAction = async (bookingId, action) => {
    try {
      const bookingRef = doc(db, 'bookings', bookingId);
      const newStatus = action === 'accept' ? 'confirmed' : 'rejected';

      await updateDoc(bookingRef, {
        status: newStatus,
        updatedAt: new Date()
      });

      // Update local state
      setBookings(bookings.map(booking => 
        booking.id === bookingId ? {...booking, status: newStatus} : booking
      ));
      setNewBookings(newBookings.filter(booking => booking.id !== bookingId));

      toast.success(`Booking ${newStatus} successfully!`);

      // Notify user about the status change (you would implement this in your notification system)
      // For now, we'll just log it
      console.log(`Notification sent to user: Your booking has been ${newStatus}`);

    } catch (error) {
      console.error(`Error ${action}ing booking:`, error);
      toast.error(`Error ${action}ing booking`);
    }
  };

  const getStatusBadge = (status) => {
    switch(status) {
      case 'pending':
        return <span className="status pending">Pending</span>;
      case 'confirmed':
        return <span className="status confirmed">Confirmed</span>;
      case 'completed':
        return <span className="status completed">Completed</span>;
      case 'rejected':
        return <span className="status rejected">Rejected</span>;
      case 'cancelled':
        return <span className="status cancelled">Cancelled</span>;
      default:
        return <span className="status unknown">Unknown</span>;
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!workerData) {
    return (
      <div className="no-profile">
        <h2>You need to create your worker profile first</h2>
        <Link to="/worker-profile" className="btn">Create Profile</Link>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome, {workerData.name}</h1>
        <p>Here's your work dashboard</p>

        {newBookings.length > 0 && (
          <div className="notification-badge">
            {newBookings.length} new booking{newBookings.length > 1 ? 's' : ''}
          </div>
        )}
      </div>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Total Bookings</h3>
          <p>{bookings.length}</p>
        </div>
        <div className="stat-card">
          <h3>Upcoming Jobs</h3>
          <p>{bookings.filter(b => b.status === 'confirmed').length}</p>
        </div>
        <div className="stat-card">
          <h3>Pending Requests</h3>
          <p>{newBookings.length}</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <section className="pending-requests">
          <h2>Pending Booking Requests</h2>
          {newBookings.length === 0 ? (
            <div className="no-requests">
              <p>You don't have any pending booking requests.</p>
            </div>
          ) : (
            <div className="requests-list">
              {newBookings.map((booking) => (
                <div key={booking.id} className="request-card">
                  <div className="request-header">
                    <h3>Booking from {booking.userName}</h3>
                    {getStatusBadge(booking.status)}
                  </div>
                  <div className="request-details">
                    <p><strong>Service:</strong> {workerData.profession}</p>
                    <p><strong>Date:</strong> {booking.date}</p>
                    <p><strong>Time:</strong> {booking.time}</p>
                    <p><strong>Duration:</strong> {booking.hours} hours</p>
                    <p><strong>Total Earnings:</strong> ${booking.total}</p>
                  </div>
                  <div className="request-actions">
                    <button 
                      className="accept-btn"
                      onClick={() => handleBookingAction(booking.id, 'accept')}
                    >
                      Accept
                    </button>
                    <button 
                      className="reject-btn"
                      onClick={() => handleBookingAction(booking.id, 'reject')}
                    >
                      Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>

        <section className="upcoming-jobs">
          <h2>Your Confirmed Jobs</h2>
          {bookings.filter(b => b.status === 'confirmed').length === 0 ? (
            <div className="no-jobs">
              <p>You don't have any confirmed jobs.</p>
            </div>
          ) : (
            <div className="jobs-list">
              {bookings
                .filter(booking => booking.status === 'confirmed')
                .map((booking) => (
                  <div key={booking.id} className="job-card">
                    <div className="job-header">
                      <h3>Booking from {booking.userName}</h3>
                      {getStatusBadge(booking.status)}
                    </div>
                    <div className="job-details">
                      <p><strong>Date:</strong> {booking.date}</p>
                      <p><strong>Time:</strong> {booking.time}</p>
                      <p><strong>Duration:</strong> {booking.hours} hours</p>
                      <p><strong>Earnings:</strong> ${booking.total}</p>
                    </div>
                    <div className="job-actions">
                      <button 
                        className="complete-btn"
                        onClick={() => handleBookingAction(booking.id, 'complete')}
                      >
                        Mark as Completed
                      </button>
                    </div>
                  </div>
                ))}
            </div>
          )}
        </section>

        <section className="quick-actions">
          <h2>Quick Actions</h2>
          <div className="actions-grid">
            <Link to="/worker-profile" className="action-card">
              <h3>Update Profile</h3>
              <p>Edit your profile information</p>
            </Link>
            <Link to="/availability" className="action-card">
              <h3>Set Availability</h3>
              <p>Update when you're available</p>
            </Link>
            <Link to="/bookings" className="action-card">
              <h3>View All Bookings</h3>
              <p>See all your bookings</p>
            </Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default WorkerDashboard;
