import { useState, useEffect } from 'react';
import { collection, query, where, getDocs, limit } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import Card from '../../components/Card';
import PricePopup from '../../components/PricePopup';
import './styles/dashboard.css';

const UserDashboard = () => {
  const [workers, setWorkers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [bookings, setBookings] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const workersRef = collection(db, 'workers');
        const q = query(workersRef, limit(4));
        const querySnapshot = await getDocs(q);

        const workersData = [];
        querySnapshot.forEach((doc) => {
          workersData.push({ id: doc.id, ...doc.data() });
        });

        setWorkers(workersData);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching workers:", error);
        setLoading(false);
      }
    };

    const fetchBookings = async () => {
      if (!user) return;

      try {
        const bookingsRef = collection(db, 'bookings');
        const q = query(bookingsRef, where('userId', '==', user.uid));
        const querySnapshot = await getDocs(q);

        const bookingsData = [];
        querySnapshot.forEach((doc) => {
          bookingsData.push({ id: doc.id, ...doc.data() });
        });

        setBookings(bookingsData);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchWorkers();
    fetchBookings();
  }, [user]);

  const handleBook = (worker) => {
    setSelectedWorker(worker);
    setShowPopup(true);
  };

  const handleBookingSubmit = async (bookingData) => {
    try {
      console.log('Booking data:', {
        ...bookingData,
        userId: user.uid,
        userName: user.name || user.email,
        status: 'confirmed',
        createdAt: new Date()
      });

      alert('Booking successful!');
      setShowPopup(false);
    } catch (error) {
      console.error('Error creating booking:', error);
      alert('Error creating booking');
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome, {user?.name || user?.email}</h1>
        <p>Here's what's happening with your account today.</p>
      </div>

      <div className="dashboard-sections">
        <section className="recommended-workers">
          <h2>Recommended Workers</h2>
          {loading ? (
            <div className="loading">Loading workers...</div>
          ) : (
            <div className="workers-grid">
              {workers.map((worker) => (
                <div key={worker.id} className="worker-card-wrapper">
                  <Card worker={worker} />
                  <button 
                    className="book-now-btn"
                    onClick={() => handleBook(worker)}
                  >
                    Book Now
                  </button>
                </div>
              ))}
            </div>
          )}
        </section>

        <section className="upcoming-bookings">
          <h2>Your Upcoming Bookings</h2>
          {bookings.length === 0 ? (
            <div className="no-bookings">
              <p>You don't have any upcoming bookings.</p>
              <button onClick={() => window.location.href = '/hire'}>Find Workers</button>
            </div>
          ) : (
            <div className="bookings-list">
              {bookings.map((booking) => (
                <div key={booking.id} className="booking-card">
                  <h3>{booking.workerName}</h3>
                  <p>Date: {booking.date}</p>
                  <p>Time: {booking.time}</p>
                  <p>Duration: {booking.hours} hours</p>
                  <p>Total: ${booking.total}</p>
                  <p>Status: <span className={`status ${booking.status}`}>{booking.status}</span></p>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>

      {showPopup && selectedWorker && (
        <PricePopup 
          worker={selectedWorker} 
          onClose={() => setShowPopup(false)}
          onBook={handleBookingSubmit}
        />
      )}
    </div>
  );
};

export default UserDashboard;
