import { useState, useEffect } from 'react';
import { doc, getDoc, collection, query, where, getDocs } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import { Link } from 'react-router-dom';
import '../../styles/dashboard.css';

const WorkerDashboard = () => {
  const [workerData, setWorkerData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [bookings, setBookings] = useState([]);
  const { user } = useAuth();

  useEffect(() => {
    const fetchWorkerData = async () => {
      if (!user) return;

      try {
        const workerRef = doc(db, 'workers', user.uid);
        const workerSnap = await getDoc(workerRef);

        if (workerSnap.exists()) {
          setWorkerData(workerSnap.data());
        } else {
          // Redirect to create profile if worker profile doesn't exist
          window.location.href = '/worker-profile';
        }
      } catch (error) {
        console.error("Error fetching worker data:", error);
      } finally {
        setLoading(false);
      }
    };

    const fetchBookings = async () => {
      if (!user) return;

      try {
        const bookingsRef = collection(db, 'bookings');
        const q = query(bookingsRef, where('workerId', '==', user.uid));
        const querySnapshot = await getDocs(q);

        const bookingsData = [];
        querySnapshot.forEach((doc) => {
          bookingsData.push({ id: doc.id, ...doc.data() });
        });

        setBookings(bookingsData);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchWorkerData();
    fetchBookings();
  }, [user]);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!workerData) {
    return (
      <div className="no-profile">
        <h2>You need to create your worker profile first</h2>
        <Link to="/worker-profile" className="btn">Create Profile</Link>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome, {workerData.name}</h1>
        <p>Here's your work dashboard</p>
      </div>

      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Total Bookings</h3>
          <p>{bookings.length}</p>
        </div>
        <div className="stat-card">
          <h3>Upcoming Jobs</h3>
          <p>{bookings.filter(b => b.status === 'confirmed').length}</p>
        </div>
        <div className="stat-card">
          <h3>Rating</h3>
          <p>{workerData.rating || 'Not rated yet'}</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <section className="upcoming-jobs">
          <h2>Your Upcoming Jobs</h2>
          {bookings.length === 0 ? (
            <div className="no-jobs">
              <p>You don't have any upcoming jobs.</p>
            </div>
          ) : (
            <div className="jobs-list">
              {bookings.map((booking) => (
                <div key={booking.id} className="job-card">
                  <h3>Booking for {booking.userName}</h3>
                  <p>Date: {booking.date}</p>
                  <p>Time: {booking.time}</p>
                  <p>Duration: {booking.hours} hours</p>
                  <p>Earnings: ${booking.total}</p>
                  <p>Status: <span className={`status ${booking.status}`}>{booking.status}</span></p>
                </div>
              ))}
            </div>
          )}
        </section>

        <section className="quick-actions">
          <h2>Quick Actions</h2>
          <div className="actions-grid">
            <Link to="/worker-profile" className="action-card">
              <h3>Update Profile</h3>
              <p>Edit your profile information</p>
            </Link>
            <Link to="/availability" className="action-card">
              <h3>Set Availability</h3>
              <p>Update when you're available</p>
            </Link>
            <Link to="/bookings" className="action-card">
              <h3>View All Bookings</h3>
              <p>See all your bookings</p>
            </Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default WorkerDashboard;
