import { useState, useEffect } from 'react';
import { doc, getDoc, setDoc, updateDoc } from 'firebase/firestore';
import { db, storage } from '../../firebase/config';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import '../../styles/worker-profile.css';

const WorkerProfile = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [profileData, setProfileData] = useState({
    name: '',
    profession: '',
    description: '',
    hourlyRate: '',
    phone: '',
    imageUrl: ''
  });
  const [imageUpload, setImageUpload] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  useEffect(() => {
    const fetchWorkerProfile = async () => {
      if (!user) return;

      try {
        const workerRef = doc(db, 'workers', user.uid);
        const workerSnap = await getDoc(workerRef);

        if (workerSnap.exists()) {
          setProfileData(workerSnap.data());
        }
      } catch (error) {
        console.error("Error fetching worker profile:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkerProfile();
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      setImageUpload(e.target.files[0]);
      setUploadError(null); // Reset error when new file is selected
    }
  };

  const uploadImage = async () => {
    if (!imageUpload) return null;

    setUploading(true);
    setUploadError(null);

    try {
      // Generate a unique filename to prevent overwrites
      const fileExtension = imageUpload.name.split('.').pop();
      const filename = `profile-${Date.now()}.${fileExtension}`;
      const imageRef = ref(storage, `worker-images/${user.uid}/${filename}`);

      // Add metadata to the file
      const metadata = {
        contentType: imageUpload.type,
      };

      const snapshot = await uploadBytes(imageRef, imageUpload, metadata);
      const downloadURL = await getDownloadURL(snapshot.ref);
      setProfileData(prev => ({ ...prev, imageUrl: downloadURL }));
      return downloadURL;
    } catch (error) {
      console.error("Error uploading image:", error);
      setUploadError("Failed to upload image. Please try again.");
      throw error;
    } finally {
      setUploading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let imageUrl = profileData.imageUrl;

      if (imageUpload) {
        try {
          imageUrl = await uploadImage();
        } catch (error) {
          // If image upload fails but other data is valid, proceed without the image
          if (!profileData.name || !profileData.profession || !profileData.hourlyRate) {
            throw error;
          }
        }
      }

      const workerRef = doc(db, 'workers', user.uid);
      const workerData = {
        ...profileData,
        id: user.uid,
        email: user.email,
        hourlyRate: parseFloat(profileData.hourlyRate) || 0,
        imageUrl,
        updatedAt: new Date()
      };

      await setDoc(workerRef, workerData, { merge: true });

      // Also update the user's name in the users collection
      const userRef = doc(db, 'users', user.uid);
      await updateDoc(userRef, { name: profileData.name });

      alert('Profile updated successfully!');
      navigate('/worker-dashboard');
    } catch (error) {
      console.error("Error updating profile:", error);
      alert(uploadError || 'Error updating profile. Please try again.');
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="worker-profile">
      <h1>Your Profile</h1>
      <p>Complete your profile to start getting hired</p>

      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <h2>Basic Information</h2>

          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={profileData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="profession">Profession</label>
            <select
              id="profession"
              name="profession"
              value={profileData.profession}
              onChange={handleChange}
              required
            >
              <option value="">Select your profession</option>
              <option value="Electrician">Electrician</option>
              <option value="Plumber">Plumber</option>
              <option value="Carpenter">Carpenter</option>
              <option value="Painter">Painter</option>
              <option value="Cleaner">Cleaner</option>
              <option value="AC Technician">AC Technician</option>
              <option value="Mechanic">Mechanic</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="hourlyRate">Hourly Rate ($)</label>
            <input
              type="number"
              id="hourlyRate"
              name="hourlyRate"
              value={profileData.hourlyRate}
              onChange={handleChange}
              min="10"
              step="5"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="phone">Phone Number</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={profileData.phone}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={profileData.description}
              onChange={handleChange}
              rows="4"
              required
            ></textarea>
          </div>
        </div>

        <div className="form-section">
          <h2>Profile Picture</h2>

          <div className="image-upload">
            {profileData.imageUrl ? (
              <img src={profileData.imageUrl} alt="Profile" className="profile-image" />
            ) : (
              <div className="image-placeholder">No image</div>
            )}

            <div className="upload-controls">
              <input
                type="file"
                id="image"
                accept="image/*"
                onChange={handleImageChange}
                disabled={uploading}
              />
              <label htmlFor="image" className="upload-btn">
                {imageUpload ? imageUpload.name : 'Choose Image'}
              </label>
              {uploading && <span>Uploading...</span>}
              {uploadError && <span className="error-message">{uploadError}</span>}
            </div>
          </div>
        </div>

        <button type="submit" disabled={uploading} className="submit-btn">
          {uploading ? 'Saving...' : 'Save Profile'}
        </button>
      </form>
    </div>
  );
};

export default WorkerProfile;
